import React from 'react';
import { AlertTriangle, Shield } from 'lucide-react';

const AlertLog = ({ alerts }) => {
  const getAlertColor = (type) => {
    if (type === 'critical') return 'bg-red-500';
    if (type === 'danger') return 'bg-orange-500';
    return 'bg-yellow-500';
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
      {/* Recent Alerts */}
      <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-gray-700">
        <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
          <AlertTriangle size={24} className="text-yellow-400" />
          Recent Alerts
        </h3>
        <div className="space-y-3">
          {alerts.length === 0 ? (
            <p className="text-gray-500 text-center py-4">No alerts yet</p>
          ) : (
            alerts.map(alert => (
              <div 
                key={alert.id} 
                className="flex items-center gap-3 p-3 bg-gray-700/50 rounded-lg border-l-4 border-l-red-500"
              >
                <div className={`w-2 h-2 rounded-full ${getAlertColor(alert.type)}`} />
                <div className="flex-1">
                  <p className="font-semibold">{alert.message}</p>
                  <p className="text-xs text-gray-400">{alert.time}</p>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Edge Security Info */}
      <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-gray-700">
        <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
          <Shield size={24} className="text-purple-400" />
          Edge Security Status
        </h3>
        <div className="space-y-4">
          <div className="flex items-center justify-between p-3 bg-green-500/20 rounded-lg border border-green-500/50">
            <span className="font-semibold">Data Encryption</span>
            <span className="text-green-400 text-sm font-bold">✓ ACTIVE</span>
          </div>
          <div className="flex items-center justify-between p-3 bg-green-500/20 rounded-lg border border-green-500/50">
            <span className="font-semibold">TLS Connection</span>
            <span className="text-green-400 text-sm font-bold">✓ SECURE</span>
          </div>
          <div className="flex items-center justify-between p-3 bg-green-500/20 rounded-lg border border-green-500/50">
            <span className="font-semibold">Cloud Sync</span>
            <span className="text-green-400 text-sm font-bold">✓ ONLINE</span>
          </div>
          <div className="p-3 bg-blue-500/20 rounded-lg border border-blue-500/50">
            <p className="text-sm text-gray-300">
              <span className="font-semibold text-blue-400">Info:</span> Semua data dienkripsi dengan AES-256 sebelum dikirim ke cloud server.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AlertLog;
