import React from 'react';
import { Power, Wind, Droplets, Bell } from 'lucide-react';

const ActuatorControl = ({ sensorData }) => {
  return (
    <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-gray-700">
      <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
        <Power size={24} className="text-green-400" />
        Actuator Status
      </h3>
      
      <div className="space-y-4">
        {/* Exhaust Fan */}
        <div className={`p-4 rounded-lg border-2 ${sensorData.exhaustFan ? 'bg-green-500/20 border-green-500' : 'bg-gray-700/50 border-gray-600'}`}>
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              <Wind className={sensorData.exhaustFan ? 'text-green-400 animate-spin' : 'text-gray-400'} size={24} />
              <span className="font-semibold">Exhaust Fan</span>
            </div>
            <span className={`px-3 py-1 rounded-full text-xs font-bold ${sensorData.exhaustFan ? 'bg-green-500 text-white' : 'bg-gray-600 text-gray-300'}`}>
              {sensorData.exhaustFan ? 'ON' : 'OFF'}
            </span>
          </div>
          <p className="text-sm text-gray-400">
            {sensorData.exhaustFan ? 'Menghisap udara berbahaya' : 'Standby mode'}
          </p>
        </div>

        {/* Water Pump */}
        <div className={`p-4 rounded-lg border-2 ${sensorData.waterPump ? 'bg-blue-500/20 border-blue-500' : 'bg-gray-700/50 border-gray-600'}`}>
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              <Droplets className={sensorData.waterPump ? 'text-blue-400 animate-pulse' : 'text-gray-400'} size={24} />
              <span className="font-semibold">Water Pump</span>
            </div>
            <span className={`px-3 py-1 rounded-full text-xs font-bold ${sensorData.waterPump ? 'bg-blue-500 text-white' : 'bg-gray-600 text-gray-300'}`}>
              {sensorData.waterPump ? 'ON' : 'OFF'}
            </span>
          </div>
          <p className="text-sm text-gray-400">
            {sensorData.waterPump ? 'Menyemprotkan air' : 'Standby mode'}
          </p>
        </div>

        {/* Alarm */}
        <div className={`p-4 rounded-lg border-2 ${sensorData.alarm ? 'bg-red-500/20 border-red-500 animate-pulse' : 'bg-gray-700/50 border-gray-600'}`}>
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              <Bell className={sensorData.alarm ? 'text-red-400 animate-bounce' : 'text-gray-400'} size={24} />
              <span className="font-semibold">Alarm System</span>
            </div>
            <span className={`px-3 py-1 rounded-full text-xs font-bold ${sensorData.alarm ? 'bg-red-500 text-white' : 'bg-gray-600 text-gray-300'}`}>
              {sensorData.alarm ? 'ALERT!' : 'OFF'}
            </span>
          </div>
          <p className="text-sm text-gray-400">
            {sensorData.alarm ? 'Sirine berbunyi!' : 'Tidak ada ancaman'}
          </p>
        </div>
      </div>
    </div>
  );
};

export default ActuatorControl;
